import { useContext, useEffect, useRef, useState } from "react";
import ShinCodeContext from "./index";
import './App.css';

const reducer = (state, action) => {
  switch (action.type) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    default:
      return state;
  } 
}

function App() {
  // useStateは状態の更新
  const [count, setCount] = useState(0); 
  const handleClick = () => {
    setCount(count + 1);
  }

  // useEffectは発火のタイミング　第二引数で発火タイミングを変更してる
  // 第二引数の配列の中に発火タイミングに合わせて動かした変数を入れてコントロール
  useEffect(() => {
    console.log("hello hooks");
  }, [count]);

  // useContextは定義したデータを自由に他コンポーネントへ出力可能
  const shincodeInfo = useContext(ShinCodeContext)

  // useRefは指定したref属性の情報を取る事ができる
  const ref = useRef();
  const handleRef = () => {
    console.log(ref.current.value); // これならinputに入力した情報を取れる
    console.log(ref.current.offsetHeight); // これならinputの高さを取れる
  }

  const [state, dispatch] = useReducer(reducer, 0);

  return (
    <div className="App">
      <h1>useState, useEffect</h1>
      <button onClick={handleClick}>+</button>
      <p>{ count }</p>
      <hr />

      <h1>useContext</h1>
      <p>{shincodeInfo.name}</p>
      <p>{shincodeInfo.age}</p>
      <hr />

      <h1>useRef</h1>
      <input type="text" ref={ref}/>
      <button onClick={handleRef}>useRef</button>
      <hr />

      <h1>useReducer</h1>
      <button onClick={() => dispatch({type : "increment"})}>+</button>


    </div>
  );
}

export default App;
