import { useEffect, useState } from "react";
import './App.css';

function App() {
  // useStateは状態の更新
  const [count, setCount] = useState(0); 
  const handleClick = () => {
    setCount(count + 1);
  }

  // useEffectは発火のタイミング　第二引数で発火タイミングを変更してる
  useEffect = (() => {
    console.log("hello hooks");
  }, []);

  return (
    <div className="App">
      <h1>useState</h1>
      <button onClick={handleClick}>+</button>
      <p>{ count }</p>
    </div>
  );
}

export default App;
