import './destyle.css';
import './App.css';
import ReactDOM from 'react-dom'
import React, { useState } from 'react'


function App() {
  const initialTodo = [
    {
      todoTitle:'初期テキスト1',
      taskDescription:'初期テキストの説明1',
      taskStatus: true
    },
    {
      todoTitle:'初期テキスト2',
      taskDescription:'初期テキストの説明2',
      taskStatus: true
    },
    {
      todoTitle:'初期テキスト3',
      taskDescription:'初期テキストの説明3',
      taskStatus: true
    }
  ]
  const [todos, setTodos] = useState(initialTodo)
  const [todoTitle, setTodoTitle] = useState('')
  // const handleNewTask = (event) => {
  //   console.log(event.target.value)
  // }
  const handleNewTask = (event) => {
    // ここで入力テキストを保持
    setTodoTitle(event.target.value)
  }
  const handleSubmit = (event) => {
    // デフォルトのイベント動作をキャンセル
    event.preventDefault()
    // 入力テキストが空であれば動作終了
    console.log('submit 空欄 動作終了')
    if ( todoTitle === '') return
    console.log('submit テキストあり')
    setTodos(todos => [...todos,{todoTitle, taskDescription: '', isCompleted: false}])
    setTodoTitle('')
  }

  return (
    <div className="App">
      <h1>TODO</h1>
      <div className="todo-box">
        <div className="todo-list">
          <ul>
            {todos.map((todo, index) => (
              <li key={index}>
                <dl>
                  <dt>{todo.todoTitle}</dt>
                  <dd>{todo.taskDescription}</dd>
                  <p>ステータスラベル</p>
                </dl>
              </li>
            ))}
            {/* <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li>
            <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li>
            <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li>
            <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li> */}
          </ul>
        </div>
        <div className="todo-input">
          <form onSubmit={ handleSubmit }>
            <label htmlFor="todoTitleText"></label>
            <input value={ todoTitle } onChange={ handleNewTask } type="text" id="todoTitleText" name="customer_name" />
            <button style={{ width: '100px' }} type="submit">送信</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
