import './destyle.css';
import './App.css';
import ReactDOM from 'react-dom'
import React, { useState, useEffect } from 'react'
import { v4 as uuidv4 } from 'uuid';


function App() {
  let uniqueId = uuidv4();
  const initialTodo = [
    {
      todoTitle:'初期テキスト1',
      todoDescription:'初期テキストの説明1',
      taskStatus: 'ラベル',
      taskID: uniqueId
    },
    {
      todoTitle:'初期テキスト2',
      todoDescription:'初期テキストの説明2',
      taskStatus: 'ラベル',
      taskID: uniqueId
    },
    {
      todoTitle:'初期テキスト3',
      todoDescription:'初期テキストの説明3',
      taskStatus: 'ラベル',
      taskID: uniqueId
    }
  ]
  const [todos, setTodos] = useState(initialTodo)
  const [todoTitle, setTodoTitle] = useState('')
  const [todoDescription, setTodoDescription] = useState('')

  useEffect(() => {
    console.log(todos)
    // console.log('UUID' + uniqueId)
  }, [todos])


  // let taskNum = todos.length
  // const taskIdIncremeant = () => {
  //   taskNum++
  //   return taskNum
  // }


  const handleNewTitle = (event) => {
    // ここで入力テキストを保持/更新
    setTodoTitle(event.target.value)
  }
  const handleNewDescription = (event) => {
    // ここで入力テキストを保持/更新
    setTodoDescription(event.target.value)
  }
  const handleSubmit = (event) => {
    // デフォルトのイベント動作をキャンセル
    event.preventDefault()
    // 入力テキストが空であれば動作終了
    console.log('submit 空欄 動作終了')
    if ( todoTitle === '' || todoDescription === '' ) return
    console.log('submit テキストあり')
    // taskIdIncremeant()
    setTodos(todos => [...todos,{todoTitle, todoDescription, taskStatus: 'ラベル', taskID: uniqueId }])
    // クリック後に入力欄のテキストを初期化
    setTodoTitle('')
    setTodoDescription('')
  }

  return (
    <div className="App">
      <h1>TODO</h1>
      <div className="todo-box">
        <div className="todo-list">
          <ul>
            {todos.map((todo, index) => (
              <li key={index}>
                <dl>
                  <dt className="todo-title">{todo.todoTitle}</dt>
                  <dd className="todo-description">{todo.todoDescription}</dd>
                  <p className="todo-status">{ todo.taskStatus }</p>
                </dl>
              </li>
            ))}
            {/* <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li>
            <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li>
            <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li>
            <li>
              <dl>
                <dt>TODOタイトル</dt>
                <dd>詳細　サンプルテキスト</dd>
                <p>tag</p>
              </dl>
            </li> */}
          </ul>
        </div>
        <div className="todo-input">
          <form onSubmit={ handleSubmit }>
            <label htmlFor="todoTitleText">task title</label>
            <input value={ todoTitle } onChange={ handleNewTitle } type="text" id="todoTitleText" name="task_name" />
            <label htmlFor="todoDescriptionText">task description</label>
            <input value={ todoDescription } onChange={ handleNewDescription } type="text" id="todoDescriptionText" name="task_description" />

            <button style={{ width: '100px' }} type="submit">送信</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
